import React from "react";
import {
    Card,
    CardImg,
    CardText,
    CardBody,
    CardTitle,
    Button
} from "reactstrap";

const KeysToComponentMap = {
    card:Card,
    img:CardImg,
    text:CardText,
    body:CardBody,
    title:CardTitle,
    button:Button
};

function marvel(config) {
    if(typeof KeysToComponentMap[config.component] !== "undefined") {
        return React.createElement(
         KeysToComponentMap[config.component],
         {
            src:config.src
         },
          config.children && 
          (typeof config.children === "string"
            ? config.children
            : config.children.map(c => marvel(c)))  
        );
    }
}

export default marvel
