import React from 'react'
import JsonData from './data.json'
import './marvel.css'
import {useState} from 'react'

function JsonDataDisplay(){
    const [characters,setCharacters] = useState(true);
    const [comics,setComics] = useState(false);
    const characterHandler = () => {
        setCharacters(true);
        setComics(false);
    };
    const comicsHandler = () => {
        setCharacters(false);
        setComics(true);
    }

    const DisplayData=JsonData.marvel.characters.map(
        (info)=>{
            return(
                <div className='j-body'>
                    <div className='image1'>
                        <img src={info.img1} alt='imageOfCard' className='cardImage'></img>
                    </div>
                    <div className='cardBody'>
                      <p className='title1'>{info.title1}</p>
                      <button className='button1'>{info.button1}</button>
                    </div>
                </div>
            )
        }
    )

    // const DisplayData2=JsonData.marvel.comics.map(
    //     (info)=>{
    //         return(
    //             <div className='j-body'>
    //                 <div className='image1'>
    //                     <img src={info.img1} className='cardImage'></img>
    //                 </div>
    //                 <div className='cardBody'>
    //                     <p className='title1'>{info.title2}</p>
    //                     {/* <button className='button1'>{info.}</button> */}
    //                 </div>
    //             </div>
    //         )
    //     }
    // )

    return(
        <div className='content-body'>
            <div className='header'>
                <img src='https://upload.wikimedia.org/wikipedia/commons/thumb/b/b9/Marvel_Logo.svg/1200px-Marvel_Logo.svg.png' alt='marvel' className='logo'></img>
                {characters && (
                <button onClick={characterHandler}>Characters</button>
                )}
                <button onClick={comicsHandler}>Comics</button>
                <h4>Stories</h4>
            </div>
            <h3 className='pageInfo'>Characters</h3>
            <div className='card1'>
                <div className='main-body'>
                    {DisplayData}
                </div>
            </div>
        </div>
    )
}

export default JsonDataDisplay;
