import React from 'react'
import JsonData from './data.json'
import './marvel.css'
function JsonDataDisplay(){
    const DisplayData=JsonData.map(
        (info)=>{
            return(
                <div className='j-body'>
                    <div className='image1'>
                        {console.log(info)}
                        <img src={info.marvel.characters.img1} className='cardImage'></img>
                    </div>
                    <div className='cardBody'>
                      <p className='title1'>{info.title1}</p>
                      <button className='button1'>{info.button1}</button>
                    </div>
                </div>
            )
        }
    )

    const DisplayData2=JsonData.map(
        (info)=>{
            return(
                <div className='j-body'>
                    <div className='image1'>
                        <img src={info.marvel.characters.img1} className='cardImage'></img>
                    </div>
                    <div className='cardBody'>
                        {/* <p className='title1'>{info.}</p> */}
                        {/* <button className='button1'>{info.}</button> */}
                    </div>
                </div>
            )
        }
    )

    return(
        <div className='content-body'>
            <div className='header'>
                <img src='https://upload.wikimedia.org/wikipedia/commons/thumb/b/b9/Marvel_Logo.svg/1200px-Marvel_Logo.svg.png' className='logo'></img>
                <h4>Characters</h4>
                <button onClick={DisplayData2}>Comics</button>
                <h4>Stories</h4>
            </div>
            <h3 style={{marginLeft:"40px",color:"white"}}>Characters</h3>
            <div className='card1'>
                <div className='main-body'>
                    {DisplayData}
                </div>
            </div>
        </div>
    )
}

export default JsonDataDisplay;
